# Operadores 

adicao = 10 + 10
print('Adição:', adicao)

subtracao = 10 - 5
print('Subtração:', subtracao)

multiplicacao = 14 * 9
print('Multiplicação:', multiplicacao)

divisao = 600 / 10.2
print('Divisão:', divisao)

divisao_inteira = 600 // 10
print('Divisão inteira:', divisao_inteira)

exponenciacao = 2 ** 10
print('Exponenciação:', exponenciacao)

modulo = 600 % 7
print('Módulo:', modulo)

modulo = 600 % 7
print(10 % 8 == 0)
print(16 % 8 == 0)
print(10 % 2 == 0)
print(15 % 2 == 0)