# if /elif / else
# se / se não se / se não


condicao1 = False
condicao2 = False
condicao3 = True
condicao4 = True

if condicao1:
    print(10 == 10)
elif condicao2:
    print('Código para a condição 2')
elif condicao3:
    print('Código para a condição 3 ser atendida')
elif condicao4:
    print('Código para condição 4 ser atendida!')
else:
    print('Nenhuma das condições foram satisfeitas')

if 10 == 10:
    print('Outro if')
    